n = int(input())
s = input()

ans = 0
for i in range(0, n):
    a = s[i]
    for j in range(i+1, n):
        b = s[j]
        if a == b:
            continue

        for k in range(j+1, n):
            c = s[k]
            if a == c or b == c or (j-i == k-j):
                continue

            ans += 1


print(ans)

